import 'package:firebase_database/firebase_database.dart';class RealDatabase {  FirebaseDatabase firebaseDatabase = FirebaseDatabase.instance;  DatabaseReference? databaseReference;  void InsertData(Map map) {    databaseReference = firebaseDatabase.ref();    databaseReference?.child("Data").child(map["Data"]).set(map);  }  Stream<DatabaseEvent> ReadData() {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Data").onValue;  }  Stream<DatabaseEvent> ReadDataFilter(String date) {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Data").orderByChild("DateTime").equalTo      (date).onValue;  }  Stream<DatabaseEvent> ReadDataMonthFilter(String date) {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Data").orderByChild("Month").equalTo      (date).onValue;  }  void UpdateData(String key,String id,String password) {    databaseReference = firebaseDatabase.ref();    databaseReference!        .child("Data")        .child(key)        .update({"id": id,"password": password});  }  void DeleteData(String key) {    databaseReference = firebaseDatabase.ref();    databaseReference!.child("Data").child(key).remove();  }  void Insert1Data(Map map) {    databaseReference = firebaseDatabase.ref();    databaseReference?.child("Delete").child(map["Data"]).set(map);  }  Stream<DatabaseEvent> Read1Data() {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Delete").onValue;  }  Stream<DatabaseEvent> Read1DataFilter(String date) {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Delete").orderByChild("DateTime").equalTo      (date).onValue;  }  Stream<DatabaseEvent> Read1DataMonthFilter(String date) {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Delete").orderByChild("Month").equalTo      (date).onValue;  }  void Delete1Data(String key) {    databaseReference = firebaseDatabase.ref();    databaseReference!.child("Delete").child(key).remove();  }}