import 'package:firebase_database/firebase_database.dart';class RealDatabase {  FirebaseDatabase firebaseDatabase = FirebaseDatabase.instance;  DatabaseReference? databaseReference;  void InsertData(Map map) {    databaseReference = firebaseDatabase.ref();    databaseReference?.child("Login").push().set(map);  }  Stream<DatabaseEvent> ReadData() {    databaseReference = firebaseDatabase.ref();    return databaseReference!.child("Login").onValue;  }  void UpdateData(String key, String id, String password) {    databaseReference = firebaseDatabase.ref();    databaseReference!        .child("Login")        .child(key)        .update({"id": id, "password": password});  }  void DeleteData(String key) {    databaseReference = firebaseDatabase.ref();    databaseReference!.child("Login").child(key).remove();  }}