import 'package:abhicreativefashion/Common/Utils/Colors_Utils.dart';import 'package:flutter/material.dart';Widget CustomTextField({  required IconData icon,  required Color color,  required String Name,  dynamic controller,  dynamic focusNode,  dynamic validator,  dynamic onSaved,  dynamic suffixIcon,  dynamic hide,  dynamic keyboardType,  required double height,  required double width,}) {  return Card(    elevation: 3,    shape: RoundedRectangleBorder(      borderRadius: BorderRadius.circular(30.0),    ),    child: SizedBox(      height: height,      width: width,      child: Theme(        data: ThemeData().copyWith(          scaffoldBackgroundColor: Colors.white,          colorScheme: ThemeData().colorScheme.copyWith(primary: red),        ),        child: TextFormField(          cursorColor: Colors.transparent,          controller: controller,          focusNode: focusNode,          validator: validator,          textAlign: TextAlign.start,          obscureText: hide ?? false,          keyboardType: keyboardType ?? TextInputType.number,          onSaved: onSaved,          decoration: InputDecoration(            icon: Padding(              padding: const EdgeInsets.only(left: 15.0),              child: Icon(icon),            ),            border: InputBorder.none,            hintText: '$Name',            hintStyle: TextStyle(              fontSize: 18,              color: color,            ),            suffixIcon: suffixIcon,          ),        ),      ),    ),  );}Widget CustomBotton({String? text, double? height, double? width, function}) {  return GestureDetector(    onTap: function,    child: Card(      elevation: 3,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(30.0),      ),      color: Colors.deepOrange,      child: Container(        height: height,        width: width,        alignment: Alignment.center,        child: Text(          text.toString(),          style: const TextStyle(            color: Colors.white,            fontSize: 20,          ),        ),      ),    ),  );}