import 'package:abhicreativefashion/Common/Utils/Colors_Utils.dart';import 'package:abhicreativefashion/Constants/String_Contants.dart';import 'package:flutter/material.dart';class Custom_AppBar extends StatelessWidget implements PreferredSizeWidget {  final Widget? bottom;  final double? height;  final double? size;  final dynamic ontap;  const Custom_AppBar({    Key? key,    this.bottom,    this.height,    this.size,    this.ontap,    required Null Function() onTap,  }) : super(key: key);  @override  Size get preferredSize => Size.fromHeight(height ?? 60);  @override  Widget build(BuildContext context) {    return const Material(      elevation: 1,      color: red,      child: SizedBox(        width: double.infinity,        child: Center(          child: Text(            AppName,            style: TextStyle(                color: white, fontSize: 25, fontWeight: FontWeight.w600),          ),        ),      ),    );  }}