import 'package:abhicreativefashion/Common/Widget/Custom_AppBar.dart';import 'package:abhicreativefashion/Common/Widget/Maltipale_Bottom.dart';import 'package:abhicreativefashion/Ui/Screen/ActiveScreen.dart';import 'package:abhicreativefashion/Ui/Screen/HomeScreen.dart';import 'package:abhicreativefashion/Ui/Screen/PickupScreen.dart';import 'package:abhicreativefashion/Ui/Screen/ProfileScreen.dart';import 'package:flutter/material.dart';class SecondScreen extends StatefulWidget {  const SecondScreen({Key? key}) : super(key: key);  @override  State<SecondScreen> createState() => _SecondScreenState();}class _SecondScreenState extends State<SecondScreen> {  int selectedIndex = 2;  static final List<Widget> _widgetOptions = <Widget>[    Home_Screen(),    const Pickup_Screen(),    const ActiveScreen(),    Profile_Screen(),  ];  @override  Widget build(BuildContext context) {    return SafeArea(      child: Scaffold(        appBar: Custom_AppBar(          onTap: () {},        ),        body: _widgetOptions.elementAt(selectedIndex),        bottomNavigationBar: Maltiple_Bottom(          selectedIndex: selectedIndex,          onItemTapped: (_onItemTapped),        ),      ),    );  }  void _onItemTapped(int index) {    setState(() {      selectedIndex = index;    });  }}