// ignore_for_file: camel_case_typesimport 'dart:async';import 'dart:math';import 'package:abhicreativefashion/Common/Image/Images.dart';import 'package:flutter/material.dart';import 'package:flutter_hooks/flutter_hooks.dart';class Splash_Screen extends HookWidget {  const Splash_Screen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    final timer = Timer(      const Duration(seconds: 3),          () {        Navigator.pushNamed(context, '/LoginScreenRoute');      },    );    final _animationController =    useAnimationController(duration: const Duration(milliseconds: 2500))      ..repeat();    return Scaffold(      backgroundColor: Colors.black,      body: AnimatedBuilder(        animation: _animationController,        child: Center(          child: Image.asset(            Images.logo,            fit: BoxFit.scaleDown,          ),        ),        builder: (context, child) {          return Transform(            alignment: Alignment.center,            transform: Matrix4.rotationZ(2 * pi * _animationController.value),            child: child,          );        },      ),    );  }}