// ignore_for_file: camel_case_typesimport 'dart:async';import 'package:abhicreativefashion/Common/Image/Images.dart';import 'package:flutter/material.dart';class Splash_Screen extends StatelessWidget {  const Splash_Screen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    final timer = Timer(        const Duration(seconds: 3),            () => Navigator.pushNamed(context, '/LoginScreenRoute'),    );    return Scaffold(      backgroundColor: Colors.black,      body: Center(        child: Image.asset(          Images.logo,          fit: BoxFit.scaleDown,        ),      ),    );  }}